mapper.select:
SELECT *
FROM post
WHERE status = 1
AND user_mail = #{email}
ORDER BY no DESC;

SELECT reply.*, user.nickname, user.img
FROM reply
INNER JOIN user
ON reply.user_mail = user.email
where reply.status = 1 and reply.post_no = #{postIndex}
ORDER BY reply.reply_no ASC, reply.no ASC;

SELECT count(*)
FROM post_like
where post_no = #{post_no}
  AND user_mail = #{user_mail}

SELECT btn_no
FROM post_like
where post_no = #{post_no}
AND user_mail = #{user_mail}
LIMIT 1

mapper.insert:
INSERT INTO post_like (user_mail, post_no, btn_no)
VALUES (
           #{user_mail},
           #{post_no},
           #{btn_no}
       )

mapper.update:
UPDATE post
SET like_cnt = like_cnt + 1
WHERE no = #{post_no}

UPDATE post
SET great_cnt = great_cnt + 1
WHERE no = #{post_no}

UPDATE post
SET sad_cnt = sad_cnt + 1
WHERE no = #{post_no}

mapper.delete:
DELETE FROM post_like
WHERE user_mail = #{user_mail}
AND post_no = #{post_no}

mapper.update:
UPDATE post
SET like_cnt = like_cnt -1
WHERE no = #{post_no}

UPDATE post
SET great_cnt = great_cnt -1
WHERE no = #{post_no}

UPDATE post
SET sad_cnt = sad_cnt -1
WHERE no = #{post_no}

UPDATE post_like
SET btn_no = #{btn_no}
WHERE user_mail = #{user_mail}
  AND post_no = #{post_no}

UPDATE post
SET like_cnt = like_cnt -1
WHERE no = #{post_no}
