mapper.insert:
INSERT INTO
    child (
           u_email, name, birth_date, img, content, sequence,  reg_date, mod_date
)
VALUES (#{u_email}, #{name}, #{birth_date}, #{img}, #{content},  #{sequence}, #{fourcuts_checked}, now(), now());

mapper.update:
UPDATE child SET name = #{name},birth_date = #{birth_date}, mod_date= now(), content = #{content}, fourcuts_checked = #{fourcuts_checked}, mod_date = now()
,img = #{img}
WHERE no = #{no}

